installation.script:
#!/bin/bash
# Pun Installation Script for Pterodactyl
set -e

cd /mnt/server

echo "================================="
echo "Pun JavaScript Runtime Installer"
echo "================================="
echo ""

# Check if Pun is available
if ! command -v pun &> /dev/null; then
    echo "ERROR: Pun is not installed in the container!"
    echo "Please ensure the Docker image ghcr.io/red-creators/pun is being used."
    exit 1
fi

echo "Pun version: $(pun --version || echo 'unknown')"
echo ""

# Clone from GitHub if GIT_REPO is provided
if [ -n "${GIT_REPO}" ]; then
    echo "Cloning repository from ${GIT_REPO}..."
    if [ -n "${GIT_BRANCH}" ]; then
        git clone --single-branch --branch ${GIT_BRANCH} ${GIT_REPO} .
    else
        git clone ${GIT_REPO} .
    fi
    echo "Repository cloned successfully."
    echo ""
fi

# Initialize project if no package.json exists
if [ ! -f package.json ]; then
    echo "No package.json found. Initializing new Pun project..."
    pun init
    echo "Project initialized."
    echo ""
fi

# Install dependencies if package.json exists and AUTO_INSTALL is enabled
if [ -f package.json ] && [ "${AUTO_INSTALL}" == "1" ]; then
    echo "Installing dependencies from package.json..."
    pun install
    echo "Dependencies installed."
    echo ""
fi

# Create default index.js if STARTUP_FILE doesn't exist
if [ ! -f "${STARTUP_FILE}" ]; then
    echo "Creating default ${STARTUP_FILE}..."
    cat > "${STARTUP_FILE}" << 'EOF'
// Pun Server Example
const port = process.env.PORT || process.env.SERVER_PORT || 3000;

console.log('Starting Pun server...');

Pun.serve({
  port: port,
  fetch(req) {
    const url = new URL(req.url);

    if (url.pathname === '/') {
      return new Response('Hello from Pun on Pterodactyl!', {
        headers: { 'Content-Type': 'text/plain' }
      });
    }

    if (url.pathname === '/health') {
      return new Response(JSON.stringify({ status: 'healthy' }), {
        headers: { 'Content-Type': 'application/json' }
      });
    }

    return new Response('Not Found', { status: 404 });
  }
});

console.log(`Server running at http://0.0.0.0:${port}`);
EOF
    echo "Default server file created."
    echo ""
fi

echo "================================="
echo "Installation complete!"
echo "================================="
echo ""
echo "Your Pun server is ready to start."
echo "Main file: ${STARTUP_FILE}"
echo "Port: ${SERVER_PORT}"
